import { BUY_CAKE } from "./cakeTypes"

const initialState = {
    numOfCakes: 10
}

const cakeReducer = (state = initialState, action) =>{
    console.log("Action type:" + action.type)
    switch(action.type){        
        case BUY_CAKE: return {numOfCakes: 2}
        default: return state
    }
}

export default cakeReducer
