import { BUY_CAKE } from "./cakeTypes"



const cakeReducer = (state, action) => {
    switch(action.type){
        case 'BUY_CAKE': 
            return {
                numbOfCakes: state.numOfCakes - 1
        }
        default: return state
    }
}

export default cakeReducer
